mapper.update:
UPDATE MOADD_EMPLEADOS
SET ID_UNIDADESORGANIC = #{idUO,jdbcType=DECIMAL}, CD_PUESTOSTRABAJO = #{cdPuestoTrabajo,jdbcType=VARCHAR}, TX_TIPO = #{tipo,jdbcType=CHAR}, FH_NOMBRAMIENTO = #{fechaNombramiento,jdbcType=TIMESTAMP}, FH_CESE = #{fechaCese,jdbcType=TIMESTAMP}, TX_TRATAMIENTO = #{tratamiento,jdbcType=VARCHAR}, TX_REFERENCIA = #{referencia,jdbcType=VARCHAR}, ID_USUARIO_CONFTREWA = #{idUsuarioConfTrewa,jdbcType=DECIMAL}
WHERE ID_EMPLEADOS = #{id,jdbcType=DECIMAL}

mapper.select:
SELECT ID_EMPLEADOS, CD_USUARIOS, ID_UNIDADESORGANIC, CD_PUESTOSTRABAJO, TX_TIPO, FH_NOMBRAMIENTO, FH_CESE, TX_TRATAMIENTO, TX_REFERENCIA, ID_USUARIO_CONFTREWA
FROM MOADD_EMPLEADOS
WHERE ID_UNIDADESORGANIC = #{idUO}

