mapper.select:
select ID_MENU, DS_NOMBRE, DS_DESCRIPCION, DS_URL, NU_ORDEN, LG_ACTIVADO, LG_ENOTRAVENTANA
from MOADD_MENU
where ID_MENU = #{id,jdbcType=DECIMAL}

mapper.update:
update MOADD_MENU
set DS_NOMBRE= #{nombre,jdbcType=VARCHAR}, DS_DESCRIPCION = #{descripcion,jdbcType=VARCHAR}, DS_URL = #{url,jdbcType=VARCHAR},
NU_ORDEN = #{orden,jdbcType=INTEGER}, LG_ACTIVADO = #{activado,typeHandler=SiNOHandler}, LG_ENOTRAVENTANA = #{enOtraVentana,typeHandler=SiNOHandler}
where ID_MENU = #{id,jdbcType=DECIMAL}
