mapper.select:
select per.ID_PERFILES, per.ID_CONVOCATORIAS, per.LG_DEFAULT, per.LG_PUBLICO, per.DS_DESCRIPCION, per.CD_NOMBRETREWA, ti.DS_NOMBRE as TIP_DS_NOMBRE, ti.DS_DESCRIPCION as TIP_DS_DESCRIPCION, ti.ID_TIPOSIDENTIFICA as TIP_ID_TIPOSIDENTIFICA, usuper.id_usuario_perfil
from moadr_usuario_perfil usuper, MOADD_PERFILES per left join MOADR_TIDENTIFIC_PERF rti on (per.ID_PERFILES = rti.ID_PERFILES) left join MOADD_TIPOSIDENTIFICA ti on (ti.ID_TIPOSIDENTIFICA = rti.ID_TIPOSIDENTIFICA)
WHERE ((per.id_perfiles = usuper.id_perfiles) and usuper.cd_usuarios=#{value}) ORDER BY per.DS_DESCRIPCION

mapper.insert:

	       SELECT MOAD_SQ_PERF.NEXTVAL FROM DUAL


    insert into MOADD_PERFILES (ID_PERFILES, ID_CONVOCATORIAS, DS_DESCRIPCION,
      CD_NOMBRETREWA, LG_DEFAULT, LG_PUBLICO)
    values (#{id,jdbcType=DECIMAL}, #{idConvocatoria,jdbcType=DECIMAL},
      #{descripcion,jdbcType=VARCHAR},#{cdNombreTrewa,jdbcType=VARCHAR}

    		,#{defecto,typeHandler=SiNOHandler}


    		,#{publico,typeHandler=SiNOHandler}

    	)

mapper.update:
 update MOADD_PERFILES
 set ID_CONVOCATORIAS = #{idConvocatoria,jdbcType=DECIMAL},
 	DS_DESCRIPCION = #{descripcion,jdbcType=VARCHAR},
   CD_NOMBRETREWA = #{cdNombreTrewa,jdbcType=VARCHAR}

		,LG_DEFAULT = #{defecto,typeHandler=SiNOHandler}


		,LG_PUBLICO = #{publico,typeHandler=SiNOHandler}

 where ID_PERFILES = #{id,jdbcType=DECIMAL}

UPDATE MOADD_PERFILES
SET LG_DEFAULT = 'N'
WHERE ID_CONVOCATORIAS = #{value}

mapper.select:
select per.ID_PERFILES, per.ID_CONVOCATORIAS, per.DS_DESCRIPCION, per.CD_NOMBRETREWA, per.LG_DEFAULT, per.LG_PUBLICO, ti.DS_NOMBRE as TIP_DS_NOMBRE, ti.DS_DESCRIPCION as TIP_DS_DESCRIPCION, ti.ID_TIPOSIDENTIFICA as TIP_ID_TIPOSIDENTIFICA from MOADD_PERFILES per
left join MOADR_TIDENTIFIC_PERF rti on (per.ID_PERFILES = rti.ID_PERFILES) left join MOADD_TIPOSIDENTIFICA ti on (ti.ID_TIPOSIDENTIFICA = rti.ID_TIPOSIDENTIFICA)
where ID_CONVOCATORIAS = #{idConvocatoria,jdbcType=DECIMAL} and CD_NOMBRETREWA = #{cdNombreTrewa,jdbcType=VARCHAR}
